import { VStack, Text, HStack, Box, Image, Heading, Center } from "@chakra-ui/react"
import { useEffect, useState } from "react";
import { SERVER_URL } from "../constants/constants";
import { get_user_profile_data } from "../api/endpoints";

const Post = ({username, description, formatted_date, like_count, likes}) => {

    const [loading, setLoading] = useState(true)
    const [profileImage, setProfileImage] = useState('')
    const [followers, setFollowers] = useState('')

    useEffect(() => {
    
        const fetchData = async () => {
            try {
                const data = await get_user_profile_data(username);
                setProfileImage(data.profile_image)
                setFollowers(data.followers)
            } catch {
                console.log('error')
            } finally {
                setLoading(false)
            }
        }
        fetchData()

    }, [])

    return (
        <VStack>
            <HStack>
                <Box boxSize='60px' border='1px solid' borderColor='gray.700' bg='white' borderRadius='full' overflow='hidden'>
                    <Image src={loading ? '' : `${SERVER_URL}${profileImage}`} boxSize='100%' objectFit='cover' />
                </Box>
                <VStack alignItems={'left'}>
                    <Heading size={'h2'}>@{username}</Heading>
                    <Text fontSize={'12px'}>Followers : {loading ? '-' : followers}</Text>
                    <Text fontSize={'12px'}>{formatted_date}</Text>
                </VStack>
            </HStack>
        </VStack>
    )
}

export default Post
